package chap98_homework;

public class _4_03_oper01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i =1; i<=10; i++) {
			if(i % 3 ==0) {
				System.out.println(i);
			} else if(i % 3 == 1) {
				System.out.println(i);
			} else if(i % 3 == 2) {
				System.out.println(i);
			}
		}
	}

}
