package chap99_etc;

public class BasicJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10;
		System.out.println(++num);
		System.out.println(++num);
		System.out.println(num--);
		System.out.println(--num);
		System.out.println(num--);
		System.out.println(--num);
		
		int a = 10;
		int b = 20;
		
		System.out.println(a < b);
		System.out.println(a > b);
	}

}
